        subgraph cluster_nodes {
            label="Worker Nodes";
            fillcolor="#2D3748";

            node1 [label="Node 1 (A-D)", image="shapes/infrastructure/server.svg"];
            node2 [label="Node 2 (E-H)", image="shapes/infrastructure/server.svg"];
            node3 [label="Node 3 (I-L)", image="shapes/infrastructure/server.svg"];
            node4 [label="Node 4 (M-P)", image="shapes/infrastructure/server.svg"];
            node5 [label="Node 5 (Q-T)", image="shapes/infrastructure/server.svg"];
            node6 [label="Node 6 (U-Z)", image="shapes/infrastructure/server.svg"];
        }

        nas [label="NAS Storage", image="shapes/infrastructure/storage.svg"];
    }

    // === Processing Layer ===
    subgraph cluster_processing {
        label="Processing (Kubernetes)";
        style=filled;
        fillcolor="#285E61";
        fontcolor=white;

        spark [label="Apache Spark", image="shapes/data/spark.svg"];
        airflow [label="Apache Airflow", image="shapes/data/airflow.svg"];
        kafka [label="Apache Kafka", image="shapes/data/kafka.svg"];
    }

    // === Rust Backtest Engine ===
    subgraph cluster_rust {
        label="Rust Backtest Engine";
        style=filled;
        fillcolor="#744210";
        fontcolor=white;

        engine [label="nova_backtest", image="shapes/software/rust.svg"];
        indicators [label="Indicators", image="shapes/software/console.svg"];
        sweep [label="Param Sweep", image="shapes/software/console.svg"];
        wfo [label="Walk-Forward", image="shapes/software/console.svg"];
