        duckdb [label="DuckDB", image="shapes/data/duckdb.svg"];
    }

    // === UI Layer ===
    subgraph cluster_ui {
        label="User Interface";
        style=filled;
        fillcolor="#2C5282";
        fontcolor=white;

        qt_app [label="Qt App", image="shapes/software/qt.svg"];
        tauri_app [label="Tauri App", image="shapes/software/tauri.svg"];
        web_api [label="FastAPI", image="shapes/software/api.svg"];
    }

    // === Edges ===
    polygon -> kafka;
    iex -> kafka;
    parquet -> nas;

    rack -> node1;
    rack -> node2;
    rack -> node3;
    rack -> node4;
    rack -> node5;
    rack -> node6;
    nas -> rack [style=dashed, label="NFS"];

    airflow -> spark [label="schedule"];
    spark -> node1 [label="tasks"];
    spark -> node2;
    spark -> node3;
    kafka -> engine [label="events"];

    engine -> indicators;
    indicators -> sweep;
    sweep -> wfo;

    engine -> duckdb [label="state"];
    wfo -> postgres [label="results"];
    node1 -> duckdb [style=dashed];

    web_api -> postgres;
    tauri_app -> web_api;
    qt_app -> postgres [style=dashed, label="direct"];
