digraph rust_engine {
    // Graph settings
    rankdir=LR;
    splines=ortho;
    nodesep=0.8;
    ranksep=1.0;
    bgcolor="#1E2127";
    fontname="Helvetica";

    // Default styles
    node [shape=none, fontname="Helvetica", fontcolor="white", fontsize=10, labelloc=b];
    edge [color="#718096", penwidth=1.5, arrowhead=vee];

    // === Data Input ===
    subgraph cluster_input {
        label="Data Input";
        style=filled;
        fillcolor="#2C5282";
        fontcolor=white;

        ohlcv [label="OHLCV Data", image="shapes/data/parquet.svg"];
        duckdb_in [label="DuckDB Partition", image="shapes/data/duckdb.svg"];
    }

    // === Indicator Engine ===
    subgraph cluster_indicators {
        label="Indicator Library";
        style=filled;
        fillcolor="#285E61";
        fontcolor=white;

        ma [label="Moving Averages", shape=component, style="filled,rounded", fillcolor="#38A169"];
        adaptive [label="Adaptive Filters", shape=component, style="filled,rounded", fillcolor="#38A169"];
        stochastic [label="Stochastic", shape=component, style="filled,rounded", fillcolor="#38A169"];
        regression [label="Regression", shape=component, style="filled,rounded", fillcolor="#38A169"];
        volume [label="Volume Profile", shape=component, style="filled,rounded", fillcolor="#38A169"];
        swing [label="Swing Detection", shape=component, style="filled,rounded", fillcolor="#38A169"];
    }

    // === Strategy Engine ===
    subgraph cluster_strategy {
        label="Strategy Engine";
        style=filled;
        fillcolor="#744210";
        fontcolor=white;

        strategy_json [label="Strategy JSON", shape=note, style=filled, fillcolor="#DD6B20", fontcolor=white];
        nova_script [label="NovaScript Parser", image="shapes/software/rust.svg"];
        entry_rules [label="Entry Rules", shape=component, style="filled,rounded", fillcolor="#DD6B20"];
        exit_rules [label="Exit Rules", shape=component, style="filled,rounded", fillcolor="#DD6B20"];
        position_sizing [label="Position Sizing", shape=component, style="filled,rounded", fillcolor="#DD6B20"];
    }

    // === Backtest Core ===
    subgraph cluster_backtest {
        label="Backtest Core";
        style=filled;
        fillcolor="#553C9A";
        fontcolor=white;

        executor [label="Trade Executor", image="shapes/software/rust.svg"];
        portfolio [label="Portfolio Tracker", shape=component, style="filled,rounded", fillcolor="#805AD5"];
        metrics [label="Metrics Calculator", shape=component, style="filled,rounded", fillcolor="#805AD5"];
    }

    // === Sweep & WFO ===
    subgraph cluster_optimization {
        label="Optimization";
        style=filled;
        fillcolor="#742A2A";
        fontcolor=white;

        sweep [label="Param Sweep", image="shapes/software/console.svg"];
        wfo [label="Walk-Forward", image="shapes/software/console.svg"];
        grid [label="Grid Search", shape=component, style="filled,rounded", fillcolor="#C53030"];
    }

    // === Output ===
    subgraph cluster_output {
        label="Output";
        style=filled;
        fillcolor="#2C5282";
        fontcolor=white;

        duckdb_out [label="DuckDB State", image="shapes/data/duckdb.svg"];
        postgres_out [label="PostgreSQL", image="shapes/data/postgres.svg"];
    }

    // === Data Flow ===
    ohlcv -> duckdb_in;
    duckdb_in -> ma;
    duckdb_in -> adaptive;
    duckdb_in -> stochastic;
    duckdb_in -> regression;
    duckdb_in -> volume;
    duckdb_in -> swing;

    ma -> entry_rules;
    adaptive -> entry_rules;
    stochastic -> entry_rules;
    regression -> exit_rules;
    volume -> position_sizing;
    swing -> exit_rules;

    strategy_json -> nova_script;
    nova_script -> entry_rules;
    nova_script -> exit_rules;

    entry_rules -> executor;
    exit_rules -> executor;
    position_sizing -> executor;
    executor -> portfolio;
    portfolio -> metrics;

    metrics -> sweep;
    sweep -> grid;
    grid -> wfo;

    executor -> duckdb_out [label="trades"];
    wfo -> postgres_out [label="results"];
}

