digraph wfo_pipeline {
    // Graph settings
    rankdir=TB;
    splines=ortho;
    nodesep=0.6;
    ranksep=0.8;
    bgcolor="#1E2127";
    fontname="Helvetica";

    // Default styles
    node [shape=none, fontname="Helvetica", fontcolor="white", fontsize=10, labelloc=b];
    edge [color="#718096", penwidth=1.5, arrowhead=vee];

    // === Strategy Input ===
    subgraph cluster_input {
        label="Strategy Configuration";
        style=filled;
        fillcolor="#2C5282";
        fontcolor=white;

        strategy [label="Strategy JSON", shape=note, style=filled, fillcolor="#3182CE", fontcolor=white];
        params [label="Param Ranges", shape=note, style=filled, fillcolor="#3182CE", fontcolor=white];
        wfo_config [label="WFO Config", shape=note, style=filled, fillcolor="#3182CE", fontcolor=white];
    }

    // === Walk-Forward Windows ===
    subgraph cluster_windows {
        label="Walk-Forward Windows";
        style=filled;
        fillcolor="#553C9A";
        fontcolor=white;

        win1_is [label="W1 In-Sample", shape=box, style="filled,rounded", fillcolor="#805AD5"];
        win1_oos [label="W1 Out-Sample", shape=box, style="filled,rounded", fillcolor="#6B46C1"];

        win2_is [label="W2 In-Sample", shape=box, style="filled,rounded", fillcolor="#805AD5"];
        win2_oos [label="W2 Out-Sample", shape=box, style="filled,rounded", fillcolor="#6B46C1"];

        win3_is [label="W3 In-Sample", shape=box, style="filled,rounded", fillcolor="#805AD5"];
        win3_oos [label="W3 Out-Sample", shape=box, style="filled,rounded", fillcolor="#6B46C1"];

        win4_is [label="W4 In-Sample", shape=box, style="filled,rounded", fillcolor="#805AD5"];
        win4_oos [label="W4 Out-Sample", shape=box, style="filled,rounded", fillcolor="#6B46C1"];
    }

    // === Optimization ===
    subgraph cluster_opt {
        label="Optimization Engine";
        style=filled;
        fillcolor="#744210";
        fontcolor=white;

        grid_search [label="Grid Search", image="shapes/software/console.svg"];
        backtest [label="Backtest Engine", image="shapes/software/rust.svg"];
        metrics [label="Metrics Calc", image="shapes/software/python.svg"];
        selector [label="Best Params", shape=diamond, style=filled, fillcolor="#DD6B20", fontcolor=white];
    }

    // === Validation ===
    subgraph cluster_validate {
        label="Validation";
        style=filled;
        fillcolor="#285E61";
        fontcolor=white;

        oos_test [label="OOS Validation", image="shapes/software/python.svg"];
        equity_curve [label="Equity Curve", shape=component, style="filled,rounded", fillcolor="#38A169"];
        drawdown [label="Drawdown", shape=component, style="filled,rounded", fillcolor="#38A169"];
    }

    // === Results ===
    subgraph cluster_results {
        label="Results";
        style=filled;
        fillcolor="#2C5282";
        fontcolor=white;

        aggregated [label="Aggregated OOS", image="shapes/data/duckdb.svg"];
        report [label="WFO Report", shape=note, style=filled, fillcolor="#3182CE", fontcolor=white];
        qualified [label="Qualified Strategy", shape=diamond, style=filled, fillcolor="#48BB78", fontcolor=white];
    }

    // === Flow ===
    strategy -> grid_search;
    params -> grid_search;
    wfo_config -> win1_is;

    win1_is -> grid_search;
    grid_search -> backtest;
    backtest -> metrics;
    metrics -> selector;
    selector -> win1_oos;
    win1_oos -> oos_test;

    win2_is -> grid_search [style=dashed];
    win3_is -> grid_search [style=dashed];
    win4_is -> grid_search [style=dashed];

    selector -> win2_oos [style=dashed];
    selector -> win3_oos [style=dashed];
    selector -> win4_oos [style=dashed];

    oos_test -> equity_curve;
    oos_test -> drawdown;

    equity_curve -> aggregated;
    drawdown -> aggregated;

    aggregated -> report;
    report -> qualified;
}
