pages.plantuml:
@startuml Data Storage
!theme strategient from ../assets/plantuml/themes

title Data Storage Layer

rectangle "Time Series" as ts {
    database "Tick DB\n(TimescaleDB)" as tick
    database "OHLCV\n(ClickHouse)" as ohlcv
}

rectangle "Reference" as ref {
    database "Reference Data\n(PostgreSQL)" as refdb
    database "Corporate Actions" as corpdb
}

rectangle "Alternative" as altstore {
    database "Alt Data Lake\n(S3/Parquet)" as lake
    database "Sentiment Store" as sentdb
}

rectangle "ML/Features" as ml {
    storage "Feature Store\n(Redis)" as feat
    database "Model Registry\n(MLflow)" as mlflow
}

tick --> feat
ohlcv --> feat
lake --> feat
feat --> mlflow

@enduml
